};

  render() {
    const {items, isLoading} = this.state;
    const itemsSortByComments = items.sort((a,b) => b.data.num_comments-a.data.num_comments);
    return (
    <div>
      <h1>Top commented</h1>
      <button type="button" stile ={{marginBottom: "15px"}}
      onClick={this.updateAutoRefresh}>
      {enableAutoRefresh ? "Stop" : "Start"} auto-refresh
        </button>
      {isLoading ? (<p>...Loading</p>) : (
        items.map(item => <Item key = {item.data.id} data={item.data}/>)
    )}
    </div>
    );
    }
  }

  export default App;
