import React from "react";

//https://www.reddit.com/r/reactjs.json?limit=100

export class App extends React.Component {
  constructor(){
    super()
    this.state={
      items: [],
      isLoading: false
    };
  }
    componentDidMount() {
      isLoading: true 
      fetch("https://www.reddit.com/r/reactjs.json?limit=100")
      .then(response => response.json())
      .then(data => {
        this.setState({
          items: data.data.children,
        isLoading: false
        })
              });
    }
  render() {
    const {items, isLoading} = this.state;
    return (
    <div>
      <h1>«Самые популярные»</h1>
      {isLoading ? <p>...Loading</p> :
     items.map(item =>(
<div 
key = {item.data.id}>
  {item.data.thumbnail &&<img src= {item.data.thumbnail} alt=""/>}

      <p>{item.data.title}</p>
      <p>Number of comments: {item.data.num_comments}</p>
      <a href={item.data.permalink} target="_blanc"> target="_blank" и rel="noopener noreferrer" 
        link
      </a>
    </div>
    ))}}
    </div>
  );
 }
}
export default App;
